function Movie({coverImg, title, summary, genres}) {
    return <div>
    <img src={coverImg} alt="movie cover image" />
    <h2>{title}</h2>
    <p>{summary}</p>
    <ul>
      {genres.map((g, idx) => <li key={idx}>{g}</li>)}
    </ul>
  </div>;
}

export default Movie;
